const DomainPage = () => {
  const { domainId } = useParams()
  const navigate = useNavigate()
  const domain = domains.find((d) => d.id === domainId)

  if (!domain) {
    return (
      <div className="min-h-screen bg-black text-white flex flex-col items-center justify-center">
        <div className="text-6xl mb-4">❌</div>
        <h2 className="text-2xl font-bold text-red-400 mb-2">Domain Not Found</h2>
        <p className="text-gray-400 mb-6">The domain you are looking for does not exist.</p>
        <button onClick={() => navigate('/explore')} className="btn-primary px-6 py-2">Back to Explore</button>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-black text-white">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <DomainTabs
          selectedDomain={domain}
          roadmaps={roadmaps}
          resources={resources}
          projects={projects}
        />
      </div>
    </div>
  )
}

